import { Button, Checkbox, FormControlLabel, FormLabel, Grid, makeStyles, Paper, Typography } from '@material-ui/core';
import React, { useEffect, useState } from 'react';
import { matchPath } from 'react-router';
import ClippedDrawer from '../../fragment/Header';
import backgroundImg from "../../../image/bg2.jpg";
import Footer from '../../fragment/Footer';
import { useDispatch, useSelector } from 'react-redux';
import { findById } from '../../../actions/products';
import Carousel from "react-material-ui-carousel";

const useStyles = makeStyles(theme => ({
    content: {
        margin: '-60px 30px 0px',
        zIndex: 3,
        position: 'relative',
        backgroundColor: '#ffffff',
        boxShadow: '0 16px 24px 2px rgb(0 0 0 / 14%), 0 6px 30px 5px rgb(0 0 0 / 12%), 0 8px 10px -5px rgb(0 0 0 / 20%)',
        borderRadius: '6px',
        padding: '70px 3%',
    },
    pageHeader: {
        height: '90vh',
        overflow: 'hidden',
        border: 0,
        margin: 0,
        display: 'flex',
        padding: 0,
        position: 'relative',
        maxHeight: '1000px',
        alignItems: 'center',
        backgroundSize: 'cover',
        backgroundPosition: 'center center'
    },
    contentHeader: {
        width: '100%',
        marginLeft: 'auto',
        marginRight: 'auto',
        paddingLeft: '15px',
        paddingRight: '15px',
        color: 'white',
    },
    group: {
        margin: '5% 0',
        padding: '1%',
        boxShadow: '0 2px 2px 0 rgb(0 0 0 / 14%), 0 3px 1px -2px rgb(0 0 0 / 20%), 0 1px 5px 0 rgb(0 0 0 / 12%)',
    },
    SecondExample : {
        width: '500px',

        Project : {
            position: 'relative',
            height: '300px',
            overflow: 'hidden',
            padding: '20px',

        CheckButton : {
            marginTop: '40px',
            color: 'white',

            fontSize: `${header-font-size}`,
            border: '3px solid white',
            textTransform: 'capitalize'

            // transition: 200ms;
        }
    }
}
}));




const ProductDetail = (props) => {

    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {

        dispatch(findById(props.match.params.id));
        console.log(props.match.params.id);
    }, []);
    const product = useSelector(state => state.products);
    console.log(product);

    const [autoPlay, setAutoPlay] = useState(true);
    const [animation, setAnimation] = useState('fade');
    const [indicator, setIndiCator] = useState(true);
    const [navButtonsAlwaysVisible, setNavButtonsAlwaysVisible] = useState(false);
    const [navButtonsAlwaysInvisible, setNavButtonsAlwaysInvisible] = useState(false);

    const items = [
        {
            name: "Lear Music Reader",
            description: "A PDF Reader specially designed for musicians.",
            color: "#64ACC8"
        },
        {
            name: "Hash Code 2019",
            description: "My Solution on the 2019 Hash Code by Google Slideshow problem.",
            color: "#7D85B1"
        },
        {
            name: "Terrio",
            description: "A exciting mobile game game made in the Unity Engine.",
            color: "#CE7E78"
        },
        {
            name: "React Carousel",
            description: "A Generic carousel UI component for React using material ui.",
            color: "#C9A27E"
        }
    ]

    function toggleAutoPlay() {
        this.setState({
            autoPlay: !this.state.autoPlay
        })
    }

    function toggleIndicators() {
        this.setState({
            indicators: !this.state.indicators
        })
    }

    function toggleNavButtonsAlwaysVisible() {
        this.setState({
            navButtonsAlwaysVisible: !this.state.navButtonsAlwaysVisible
        })
    }

    function toggleNavButtonsAlwaysInvisible() {
        this.setState({
            navButtonsAlwaysInvisible: !this.state.navButtonsAlwaysInvisible
        })
    }

    function changeAnimation(event) {
        this.setState({
            animation: event.target.value
        })
    }

    function changeTimeout(event, value) {
        this.setState({
            timeout: value
        })
    }

    const carouselItem = (item) => {

        return (
            <Paper
                className="Project"
                style={{
                    backgroundColor: props.item.color,
                }}
                elevation={10}
            >
                <h2>{props.item.name}</h2>
                <p>{props.item.description}</p>
    
                <Button className="CheckButton">
                    Check it out!
                </Button>
            </Paper>
        )
    }

    const carousel = (items) => {

        return (
            <div style={{ marginTop: "50px", color: "#494949" }}>
                <h2>Example: Learus Projects (random)</h2>

                <Carousel
                    className={classes.SecondExample}
                    autoPlay={this.state.autoPlay}
                    animation={this.state.animation}
                    indicators={this.state.indicators}
                    timeout={this.state.timeout}
                    navButtonsAlwaysVisible={this.state.navButtonsAlwaysVisible}
                    navButtonsAlwaysInvisible={this.state.navButtonsAlwaysInvisible}

                >
                    {
                        items.map((item, index) => {
                            carouselItem(item, index)
                        })
                    }
                </Carousel>


                <FormLabel component="legend">Options</FormLabel>
                <FormControlLabel
                    control={
                        <Checkbox onChange={this.toggleAutoPlay} checked={this.state.autoPlay} value="autoplay" color="primary" />
                    }
                    label="Auto-play"
                />
                <FormControlLabel
                    control={
                        <Checkbox onChange={this.toggleIndicators} checked={this.state.indicators} value="indicators" color="primary" />
                    }
                    label="Indicators"
                />
                <FormControlLabel
                    control={
                        <Checkbox onChange={this.toggleNavButtonsAlwaysVisible} checked={this.state.navButtonsAlwaysVisible} value="indicators" color="primary" />
                    }
                    label="NavButtonsAlwaysVisible"
                />

                <FormControlLabel
                    control={
                        <Checkbox onChange={this.toggleNavButtonsAlwaysInvisible} checked={this.state.navButtonsAlwaysInvisible} value="indicators" color="primary" />
                    }
                    label="NavButtonsAlwaysInvisible"
                />


                <FormControlLabel
                    control={
                        <RadioGroup name="animation" value={this.state.animation} onChange={this.changeAnimation} row style={{ marginLeft: "10px" }}>
                            <FormControlLabel value="fade" control={<Radio color="primary" />} label="Fade" />
                            <FormControlLabel value="slide" control={<Radio color="primary" />} label="Slide" />
                        </RadioGroup>
                    }
                />

                <FormControlLabel
                    control={
                        <div style={{ width: 300 }}>
                            <Typography id="discrete-slider" gutterBottom>
                                Animation Duration (Timeout) in ms
                            </Typography>
                            <Slider
                                defaultValue={500}
                                getAriaValueText={() => `${this.state.timeout}ms`}
                                aria-labelledby="discrete-slider"
                                valueLabelDisplay="auto"
                                step={100}
                                marks
                                min={100}
                                max={2000}
                                onChange={this.changeTimeout}
                            />
                        </div>
                    }
                />

            </div>
        )
    
    }
    return (
        <div>
            <ClippedDrawer />
            <div className={classes.content}>
                {carousel()}
            </div>
            <Footer />
        </div>
    );
}

export default ProductDetail;
