import { Card, CardMedia, CircularProgress, makeStyles, Paper } from '@material-ui/core';
import React from 'react';
import Carousel from 'react-material-ui-carousel';


const useStyles = makeStyles(theme => ({

    media: {
        height: 0,
        paddingTop: '56.25%',
        backgroundColor: 'rgba(0, 0, 0, 0.5)',
        backgroundBlendMode: 'darken',
    },
}))

const ProductDetailContent = ({ product }) => {

    const classes = useStyles();
    console.log(product);
    return (
        <div>
            <h1>{product.name}</h1>
            <Carousel
                autoPlay={false}
                animation={'fade'}
                autoPlay={true}
                indicators={true}
                navButtonsAlwaysVisible={false}
                navButtonsAlwaysInvisible={false}

            >
                {
                    product.images.map(url => {
                        return (
                            <Card>
                                <CardMedia className={classes.media} style={{ maxWidth: '500px', minHeight: '500px' }}
                                    image={`${url}`}
                                    title={product.name}
                                />

                            </Card>
                        )
                    })
                }
            </Carousel>
        </div>
    )
}

export default ProductDetailContent;
