import { Button, Card, CardMedia, CircularProgress, Divider, Grid, Input, List, ListItem, ListItemIcon, ListItemText, makeStyles, Paper, TextField, Typography } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import { Send } from '@material-ui/icons';
import React, { useState } from 'react';
import Carousel from 'react-material-ui-carousel';


const useStyles = makeStyles(theme => ({

    media: {
        height: 0,
        paddingTop: '56.25%',
        backgroundBlendMode: 'darken',
    },
    infomation: {
        padding: '2%',
        '& li': {
            margin: '2%',
            lineHeight: 2,
        }
    },
    detailInfomation: {

    },
    rowAdd: {
        height: '30px',
        textAlign: 'center',
        '& div': {
            height: '100%',
        },
    },

    review: {
        padding: '2%',
    },
    details: {
        padding: '2% 10%',
        textAlign: 'center',

    },

    warranty: {
        border: '1px solid',
        padding: '1%',
        textAlign: 'center'
    }
}))

const ProductDetailContent = ({ product }) => {

    const classes = useStyles();

    const [numberBuy, setNumberBuy] = useState(0);

    const changeNumberBuy = (e) => {
        e.preventDefault();
        console.log(e.target.value);
        setNumberBuy(e.target.value);
    }

    const increaseNumber = (e) => {
        e.preventDefault();
        setNumberBuy(numberBuy + 1);
    }
    const decreaseNumber = (e) => {
        e.preventDefault();
        if (numberBuy > 0) {
            setNumberBuy(numberBuy - 1);
        }
    }

    const showModalDetail = () => {

    }

    const modalDetail = () => {
    }

    return (
        <div>
            <div className={classes.review}>
                <Grid container >
                    <Grid item xs={5}>

                        <Carousel
                            autoPlay={false}
                            animation={'fade'}
                            autoPlay={false}
                            indicators={true}
                            navButtonsAlwaysVisible={false}
                            navButtonsAlwaysInvisible={false}

                        >
                            {
                                product.images.map(url => {
                                    return (
                                        <Card>
                                            <CardMedia className={classes.media} style={{ maxWidth: '500px', minHeight: '100%' }}
                                                image={`${url}`}
                                                title={product.name}
                                            />

                                        </Card>
                                    )
                                })
                            }
                        </Carousel>
                    </Grid>
                    <Grid item xs={7} style={{ padding: '2%' }}>
                        <Typography variant="h4" color="secondary"> {`${product.name}`} </Typography>
                        <Divider />
                        <Typography variant="h6">{product.status}</Typography>
                        <Grid container>
                            <Grid item xs={6} md={6}>
                                <List dense={'dense'} className={classes.infomation}>
                                    <ListItem>
                                        <ListItemIcon>
                                            <Send />
                                        </ListItemIcon>
                                        <ListItemText>
                                            {`${product.display}`}
                                        </ListItemText>
                                    </ListItem>
                                    <ListItem>
                                        <ListItemIcon>
                                            <Send />
                                        </ListItemIcon>
                                        <ListItemText>
                                            {`${product.cpu}`}
                                        </ListItemText>
                                    </ListItem>
                                    <ListItem>
                                        <ListItemIcon>
                                            <Send />
                                        </ListItemIcon>
                                        <ListItemText>
                                            {`${product.ram}`}
                                        </ListItemText>
                                    </ListItem>
                                    <ListItem>
                                        <ListItemIcon>
                                            <Send />
                                        </ListItemIcon>
                                        <ListItemText>
                                            {`${product.description}`}
                                        </ListItemText>
                                    </ListItem>
                                    <ListItem>
                                        <div>
                                            <button type="button" class="btn btn-primary btn-lg" data-toggle="modal" data-target="#modalDetail" >
                                                Launch demo modal
                                            </button>
                                            <div class="modal fade" id="modalDetail" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" >
                                                <div class="modal-dialog" role="document" style={{zIndex: 5000}}>
                                                    <div class="modal-content">
                                                        <div class="modal-header">
                                                            <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>
                                                            <h4 class="modal-title" id="myModalLabel">Modal title</h4>
                                                        </div>
                                                        <div class="modal-body">
                                                            ...
                                                        </div>
                                                        <div class="modal-footer">
                                                            <button type="button" class="btn btn-default" data-dismiss="modal">Close</button>
                                                            <button type="button" class="btn btn-primary">Save changes</button>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </ListItem>
                                </List>

                            </Grid>
                            <Grid item xs={6} sytle={{ padding: '2%' }}>
                                <div className={classes.warranty}>
                                    Chính sách bảo hành
                                </div>
                            </Grid>
                        </Grid>
                        <Grid container className={classes.rowAdd}>
                            <Grid item xs={5}>
                                <Grid container >
                                    <Grid item xs={3}>
                                        <Button variant="contained" onClick={increaseNumber} color="primary" style={{ height: '100%' }}>+</Button>
                                    </Grid>
                                    <Grid item xs={4}>
                                        <TextField id="numberBuy" variant="outlined" onChange={changeNumberBuy} value={numberBuy} type="number" />
                                    </Grid>
                                    <Grid item xs={3}>
                                        <Button variant="contained" onClick={decreaseNumber} color="primary" style={{ height: '100%' }}>-</Button>
                                    </Grid>
                                </Grid>
                            </Grid>
                            <Grid item xs={7}>

                                <Button variant="contained" color="secondary" style={{ float: 'left', height: '100%' }}>Thêm vào giở hàng</Button>

                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
            </div>

            <div>
                <div className={classes.details} style={{ marginTop: '5%' }}>
                    <Typography variant="h1">Các thông tin khác</Typography>
                </div>
            </div>

            <div>

            </div>
        </div>
    )
}

export default ProductDetailContent;
