import { Grid, makeStyles, Typography } from '@material-ui/core';
import React, { useEffect } from 'react';
import { matchPath } from 'react-router';
import ClippedDrawer from '../fragment/Header';
import backgroundImg from "../../image/bg2.jpg";
import Footer from '../fragment/Footer';

const useStyles = makeStyles(theme => ({
    pageHeader: {
        height: '90vh',
        overflow: 'hidden',
        border: 0,
        margin: 0,
        display: 'flex',
        padding: 0,
        position: 'relative',
        maxHeight: '1000px',
        alignItems: 'center',
        backgroundSize: 'cover',
        backgroundPosition: 'center center'
    },
    contentHeader: {
        width: '100%',
        marginLeft: 'auto',
        marginRight: 'auto',
        paddingLeft: '15px',
        paddingRight: '15px',
        color: 'white',
    },
}))


const ProductDetail = (props) => {

    const classes = useStyles();
    
    useEffect( () => {
        console.log(props.match.params.id)
    } , []);


    return (
        <div>
            <ClippedDrawer />
            <div className={classes.pageHeader} style={{ backgroundImage: `url(${backgroundImg})` }}>
                <div className={classes.contentHeader}>
                    <Grid container justify="center" alignItems="center">
                        <Grid item>
                            <Typography variant="h1" component="h2" style={{textAlign: 'center'}}>Nova-Computer</Typography>
                        </Grid>
                    </Grid>
                </div>
            </div>
            <div className={classes.content}>

            </div>
            <Footer />
        </div>
    );
}

export default ProductDetail;
