import { Card, CardMedia, CircularProgress, Grid, List, ListItem, ListItemIcon, ListItemText, makeStyles, Paper, Typography } from '@material-ui/core';
import { Send } from '@material-ui/icons';
import React from 'react';
import Carousel from 'react-material-ui-carousel';


const useStyles = makeStyles(theme => ({

    media: {
        height: 0,
        paddingTop: '56.25%',
        backgroundBlendMode: 'darken',
    },
    infomation: {
        padding: '2%',
        '>: li' : {
            padding: '1%',
            lineHeight: 2,
        }
    },
    detailInfomation: {
        
    }
}))

const ProductDetailContent = ({ product }) => {

    const classes = useStyles();
    console.log(product);
    return (
        <div>
            <Grid container >
                <Grid item xs={6}>
                    <h1>{product.name}</h1>
                    <Carousel
                        autoPlay={false}
                        animation={'fade'}
                        autoPlay={false}
                        indicators={true}
                        navButtonsAlwaysVisible={false}
                        navButtonsAlwaysInvisible={false}

                    >
                        {
                            product.images.map(url => {
                                return (
                                    <Card>
                                        <CardMedia className={classes.media} style={{ maxWidth: '500px', minHeight: '100%' }}
                                            image={`${url}`}
                                            title={product.name}
                                        />

                                    </Card>
                                )
                            })
                        }
                    </Carousel>
                </Grid>
                <Grid item xs={6}>
                    <h1>Thông thông tin cơ bản</h1>
                    <Typography>{product.status}</Typography>
                    <Grid container>
                        <Grid item xs={12} md={6}>
                            <List dense={'dense'}>
                                <ListItem>
                                    <ListItemIcon>
                                        <Send />
                                    </ListItemIcon>
                                    <ListItemText>
                                        
                                    </ListItemText>
                                </ListItem>
                            </List>
                        </Grid>
                    </Grid>
                </Grid>
            </Grid>
        </div>
    )
}

export default ProductDetailContent;
