import React, { useEffect } from 'react';
import { Container, Grid, Grow, makeStyles, Toolbar } from '@material-ui/core';
import ClippedDrawer from '../fragment/Header.js';
import { useDispatch } from 'react-redux';
import { getProducts } from '../../actions/products.js';
import Products from './products/Products.js';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
    },
    content: {
        flexGrow: 1,
        marginTop: 64,
        paddingLeft: 16
    },
    itemContainer: {
        flexWrap: 'unset',
        margin: 'unset',
        marginTop: '5px'
    },
    itemBody: {
        padding: "0 5%",
        marginTop: 0,
    }
}));

function Home() {
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getProducts());
    }, []);

    return (
        <div className={classes.root}>
            <ClippedDrawer />
            <Container className={classes.content}>
                <Grow in className={classes.itemBody}>
                    <Grid container justify="space-between" alignItems="stretch" spacing={3} className={classes.itemContainer}>
                        <Products />
                    </Grid>
                </Grow>
            </Container>
        </div>
            
    );
}

export default Home;
