import React, { useEffect } from 'react';
import { AppBar, Container, Grid, Grow, makeStyles, Toolbar, Typography } from '@material-ui/core';
import ClippedDrawer from '../fragment/Header.js';
import { useDispatch } from 'react-redux';
import { getProducts } from '../../actions/products.js';
import Products from './products/Products.js';
import backgroundImg from '../../image/bg2.jpg';

const useStyles = makeStyles((theme) => ({
    content: {
        margin: '-60px 30px 0px',
        zIndex: 3,
        position: 'relative',
        backgroundColor: '#ffffff',
        boxShadow: '0 16px 24px 2px rgb(0 0 0 / 14%), 0 6px 30px 5px rgb(0 0 0 / 12%), 0 8px 10px -5px rgb(0 0 0 / 20%)',
        borderRadius: '6px',
        padding: '70px 3%',
    },
    itemContainer: {
        flexWrap: 'unset',
        margin: 'unset',
        marginTop: '5px'
    },
    pageHeader: {
        height: '90vh',
        overflow: 'hidden',
        border: 0,
        margin: 0,
        display: 'flex',
        padding: 0,
        position: 'relative',
        maxHeight: '1000px',
        alignItems: 'center',
        backgroundSize: 'cover',
        backgroundPosition: 'center center'
    },
    contentHeader: {
        width: '100%',
        marginLeft: 'auto',
        marginRight: 'auto',
        paddingLeft: '15px',
        paddingRight: '15px',
        color: 'white',
    }
}));

function Home() {
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getProducts());
    }, []);

    return (
        <div>
            <ClippedDrawer />
            <div className={classes.pageHeader} style={{ backgroundImage: `url(${backgroundImg})` }}>
                <div className={classes.contentHeader}>
                    <Grid container justify="center" alignItems="center">
                        <Grid item>
                            <Typography variant="h1" component="h2" style={{textAlign: 'center'}}>NoVa Computer</Typography>
                        </Grid>
                    </Grid>
                </div>
            </div>
            <div className={classes.content}>

                <div>
                    <AppBar position={'static'}>
                        <Typography variant="h4">Sản phẩm bán chạy nhất</Typography>
                    </AppBar>
                    <Grow in>
                        <Grid className={classes.itemContainer}>
                            <Products />
                        </Grid>
                    </Grow>
                </div>
            </div>
        </div>

    );
}

export default Home;
