import React, { useEffect } from 'react';
import { Container, Grid, Grow, makeStyles, Toolbar } from '@material-ui/core';
import ClippedDrawer from '../fragment/Header.js';
import { useDispatch } from 'react-redux';
import { getProducts } from '../../actions/products.js';
import Products from './products/Products.js';
import backgroundImg from '../../image/bg2.jpg';

const useStyles = makeStyles((theme) => ({
    root: {
        //display: 'flex',
    },
    content: {
        margin: '-60px 0px',
        zIndex: 3,
        position: 'relative',
        backgroundColor: '#ffffff',

    },
    itemContainer: {
        flexWrap: 'unset',
        margin: 'unset',
        marginTop: '5px'
    },
    itemBody: {
    },
    pageHeader: {
        height: '90vh',
        overflow: 'hidden',
        border: 0,
        margin: 0,
        display: 'flex',
        padding: 0,
        position: 'relative',
        maxHeight: '1000px',
        alignItems: 'center',
        backgroundSize: 'cover',
        backgroundPosition: 'center center'
    }
}));

function Home() {
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getProducts());
    }, []);

    return (
        <div>
            <div className={classes.pageHeader} style={{ backgroundImage: `url(${backgroundImg})` }}>
                <ClippedDrawer />
            </div>
           
            <Container className={classes.content}>
                <Grow in className={classes.itemBody}>
                    <Grid container justify="space-between" alignItems="stretch" className={classes.itemContainer}>
                        <Products />
                    </Grid>
                </Grow>
            </Container>
        </div>

    );
}

export default Home;
