import * as api from '../services/productService.js';

//Action create
export const getProducts = () => async (dispatch) => {
    
    try {
        const { data } = await api.fetchProducts();
        dispatch({ type: FETCH_ALL, payload: data });
    } catch (error) {
        console.log(error);
    }
}
